	-moz-border-radius: 4px; 	border-radius: 4px; 	-webkit-box-shadow: inset 0 1px 0 rgba( 255, 255, 255, 0.15 ), 0 0 3px rgba( 0, 0, 0, 0.5 ); 	-moz-box-shadow: inset 0 1px 0 rgba( 255, 255, 255, 0.15 ), 0 0 3px rgba( 0, 0, 0, 0.5 ); 	box-shadow: inset 0 1px 0 rgba( 255, 255, 255, 0.15 ), 0 0 3px rgba( 0, 0, 0, 0.5 ); 	background-image: -ms-linear-gradient(top, #444444 0%, #111111 100%); 	background-image: -moz-linear-gradient(top, #444444 0%, #111111 100%); 	background-image: -o-linear-gradient(top, #444444 0%, #111111 100%); 	background-image: -webkit-gradient(linear, left top, left bottom, color-stop(0, #444444), color-stop(1, #111111)); 	background-image: -webkit-linear-gradient(top, #444444 0%, #111111 100%); 	background-image: linear-gradient(to bottom, #444444 0%, #111111 100%); }  #wonderpluginaudio-37 .amazingaudioplayer-volume-bar-adjust { 	background-color: #222; 	-webkit-box-shadow: -1px -1px 0 rgba( 0, 0, 0, 0.5 ), 1px 1px 0 rgba( 255, 255, 255, 0.1 ); 	-moz-box-shadow: -1px -1px 0 rgba( 0, 0, 0, 0.5 ), 1px 1px 0 rgba( 255, 255, 255, 0.1 ); 	box-shadow: -1px -1px 0 rgba( 0, 0, 0, 0.5 ), 1px 1px 0 rgba( 255, 255, 255, 0.1 ); }  #wonderpluginaudio-37 .amazingaudioplayer-volume-bar-adjust-active { 	background-color: #f0f0f0; 	-webkit-box-shadow: inset 0 0 5px rgba( 255, 255, 255, 0.5 ); 	-moz-box-shadow: inset 0 0 5px rgba( 255, 255, 255, 0.5 ); 	box-shadow: inset 0 0 5px rgba( 255, 255, 255, 0.5 ); }  #wonderpluginaudio-37 .amazingaudioplayer-tracklist { 	display: block; 	position: relative; }  #wonderpluginaudio-37 .amazingaudioplayer-tracklist-container { 	display: block; 	position: relative; 	margin: 4px; 	padding: 8px 0px; }  #wonderpluginaudio-37 .amazingaudioplayer-tracks-wrapper { 	position: relative; }  #wonderpluginaudio-37 .amazingaudioplayer-tracks { 	position: relative; 	list-style-type: none; 	margin: 0; 	padding: 0; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item { 	list-style-type: none; 	position: relative; 	cursor: pointer; 	color: #999; 	font-family: \"Open Sans\", Arial, sans-serif; 	font-size: 12px; 	text-shadow: 0 1px 1px rgba(0, 0, 0, 0.6); 	line-height: 22px; 	margin: 0; 	padding: 0; 	text-align: left; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item-active { 	cursor: pointer; 	color: #fff; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item a { 	color: #999;     text-decoration: none; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item-active a { 	color: #fff;     text-decoration: none; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item-duration { 	position: absolute; 	top: 0; 	right: 0; }  #wonderpluginaudio-37 .amazingaudioplayer-tracklist-arrow-prev { 	position: absolute; 	bottom: -5px; 	left: 50%; 	margin-left: -64px; }  #wonderpluginaudio-37 .amazingaudioplayer-tracklist-arrow-next { 	position: absolute; 	bottom: -5px; 	left: 50%; 	margin-left: 16px; }  #wonderpluginaudio-37 .amazingaudioplayer-tracklist-clear { 	clear: both; }  #wonderpluginaudio-37 .amazingaudioplayer-item-id { 	float: left; 	width: 18px; }  #wonderpluginaudio-37 .amazingaudioplayer-item-info { 	float: right; 	width: 36px; }  #wonderpluginaudio-37 .amazingaudioplayer-item-title { 	overflow: hidden; }  #wonderpluginaudio-37 .amazingaudioplayer-track-item:before, #wonderpluginaudio-37 .amazingaudioplayer-track-item:after { 	display: none; }  #wonderpluginaudio-37 ul, #wonderpluginaudio-37 li { 	list-style-type: none; }</style><div class=\"wonderpluginaudio\" id=\"wonderpluginaudio-37\" data-audioplayerid=\"37\" data-width=\"300\" data-height=\"600\" data-skin=\"darkbox\" data-autoplay=\"false\" data-random=\"false\" data-forceflash=\"false\" data-forcehtml5=\"false\" data-autoresize=\"false\" data-responsive=\"false\" data-showtracklist=\"true\" data-tracklistscroll=\"true\" data-showprogress=\"true\" data-showprevnext=\"true\" data-showloop=\"true\" data-preloadaudio=\"true\" data-showtime=\"false\" data-showvolume=\"true\" data-showvolumebar=\"true\" data-showliveplayedlist=\"false\" data-show